# Generate GT table
table <- sample_size %>%
  filter(COUNTY == county) %>%
  mutate(COUNTY = str_to_sentence(COUNTY))%>%
  select(-COUNTY) %>%
  adorn_totals() %>%
  gt() %>%
  tab_style(
    style = list(cell_borders(sides = "all", color = "gray", weight = px(1)),
                 cell_text(weight = "bold", color = "black")),
    locations = cells_body()
  ) %>%
  fmt_number(
      columns = c("Sample size"),
      decimals = 0,
      use_seps = TRUE
    ) %>%
  tab_style(
    style = list(
      cell_text(weight = "bold", color = "black"),
      cell_fill(color = "#FFFDD0")
    ),
    locations = cells_column_labels(everything())
  ) %>%
  tab_style(
    style = list(
      cell_text(weight = "bold", color = "black"),
      cell_fill(color = "#FFFDD0")
    ),
    locations = cells_column_spanners()
  ) %>%
  opt_table_font(
    font = list(
      google_font(name = "Cambria")
    )
  )%>%
  cols_width(
    everything() ~ px(100)
  )%>%
tab_options(
    table.width = pct(100),
    data_row.padding = px(20), # Increase padding for better readability
  )

# Print the table
table
