## PROGRESS TOWARDS 95-95-95

### Unconditional 95-95-95


::: {.cell}

```{.r .cell-code .hidden}
# Filter and prepare the data
filtered_data <- unconditional_95s %>%
  filter(COUNTY == county) %>%
  pivot_longer(cols = AWARE_D:SUPPRESSED_PERCENT, 
               names_to = "name", values_to = "value") %>%
  filter(grepl("_D|PERCENT", name)) %>%
  mutate(data = case_when(
    str_detect(name, "_D") ~ "D",
    str_detect(name, "PERCENT") ~ "PERCENT",
    TRUE ~ NA_character_
  )) 




# Define bar color
# Create the plot
ggplot(filtered_data %>% filter(data == "PERCENT"), 
       aes(x = name, y = value)) +
  geom_bar(stat = "identity", fill = bar_color) +
  geom_text(aes(label = scales::percent(value, accuracy = 0.1)), 
            vjust = -0.5, color = "black", size = 3.5) +
  ylim(0,1.2) +
  facet_wrap(~ TYPOLOGY) +
  theme_minimal() +
  theme(
    strip.background = element_rect(fill = "lightblue", color = "black"),
    strip.text = element_text(face = "bold", size = 14)
  ) +
  labs(title = "95-95-95 Cascade",
       x = "Metric",
       y = "Percentage",
       fill = "Metric") +
  scale_x_discrete(labels = c("AWARE_PERCENT" = "Awareness", 
                              "ON_ART_PERCENT" = "On ART", 
                              "SUPPRESSED_PERCENT" = "Suppressed"))
